import React, { useState } from "react";
import QuizCard from "./components/QuizCard";

import { QuestionState, fetchQuestions, Difficulty } from "./API";

const TOTAL_QUESTIONS = 10;

type Answer = {
  question: string;
  answer: string;
  correct: boolean;
  correct_answer: string;
};
const enum state {
  INIT = "initial",
  LOADING = "loading",
  SUCCESS = "success",
  ERROR = "error",
}

function App() {
  const [appState, setAppState] = useState(state.INIT);
  const [questions, setQuestions] = useState<QuestionState[]>([]);
  const [answers, setAnswers] = useState([]);
  const [number, setNumber] = useState(0);
  const [userAnswers, setUserAnswers] = useState<Answer[]>([]);
  const [score, setScore] = useState(0);
  const [gameOver, setGameover] = useState(true);

  const startTrivia = async () => {
    setAppState(state.LOADING);
    setGameover(false);
    try {
      const quizQuestions = await fetchQuestions(
        TOTAL_QUESTIONS,
        Difficulty.EASY
      );
      console.log("transformaed output:", quizQuestions);
      setAppState(state.SUCCESS);
      setQuestions(quizQuestions);
      setAnswers(quizQuestions.map((question: QuestionState) => question.answers))
    } catch {
      setAppState(state.ERROR);
    }
  };

  const checkAnswer = (e: React.MouseEvent<HTMLButtonElement>) => {};

  const nextQuestion = () => {

    if(number === (TOTAL_QUESTIONS - 1)){
      setGameover(true)
    }
    setNumber( number + 1);
  };

  return (
    <div className="App">
      {appState === state.ERROR && <p> Opps something went wrong..</p>}
      <h1>REACT QUIZ APP</h1>
      <button className="start-button" onClick={startTrivia}>
        Start Quiz
      </button>
      <p className="score">Score:{score}</p>
      {appState === state.LOADING &&  <p className="loader">Loading Questions...</p>}

      {appState === state.SUCCESS && (
        <>
          {/* <QuizCard
            questionNumber={number + 1}
            question={questions[number].question}
            answers={answers[number].answers}
            totalQuestion={TOTAL_QUESTIONS}
            userAnswer={userAnswers ? userAnswers[number] : undefined}
            callback={checkAnswer}
          /> */}
          <button onClick={nextQuestion}>Next Question</button>
        </>
      )}
    </div>
  );
}

export default App;
